import { useState, useEffect } from "react";
import ProductCard from "./ProductCard";
import api from "./api";

function BestProduct() {
    const [ items, setItems ] = useState([])
    const [ logingError, setLodingError ] = useState(null)
    const [ logingErrorTag, setLodingErrorTag] = useState(false)
    const [ pageSize, setPageSize ] = useState(4)
    const [windowWidth, setWindowWidth] = useState(window.innerWidth);

    // 화면 크기 변경 이벤트 핸들러
    const handleResize = () => {
        setWindowWidth(window.innerWidth);
    };

    //api 호출
    const handleItemList = async (params) => {
        try {
            setLodingError(null);
            setLodingErrorTag(false)
            const { list } = await api(params);
            setItems(list);
        } catch(e) {
            setLodingError(e);
            setLodingErrorTag(true)
            return;
        }
    };

    //첫 랜더링 시 실행
    useEffect(() => {
        window.addEventListener('resize', handleResize);

        handleItemList({orderBy: 'favorite', pageSize: pageSize})

        return () => {
            window.removeEventListener('resize', handleResize);
            };
    }, [ pageSize ])

    if(windowWidth >= 100)

    return (
        <>
        {logingError?.message && <span>{logingError.message}</span>}
        {logingErrorTag || 
        <div className="ProductMobile">
            <div className='bestProductContaner'>
                <h2 className='productFont bestProductFont'>베스트 상품</h2>
                <ProductCard items={items} variant='bestProduct' />
            </div>

        </div>
        }
        </>
    );
}

export default BestProduct;
