import './SaleProductStyle.css'
import { useState, useEffect } from "react";
import ProductCard from "./ProductCard";
import Api from "./api";

function SaleProduct() {
    const [ items, setItems ] = useState([])

    //Api 호출
    const handleItemList = async (params) => {
        const { list } = await Api(params);
        setItems(list);
    };

    //첫 랜더링 시 실행
    useEffect(() => {
        handleItemList({orderBy: 'recent', pageSize: 10})
    }, [])

    return (
        <div className='saleProductContaner'>
            <div>

            </div>
            <h2 className='productFont'>판매 중인 상품</h2>
            <ProductCard items={items} variant='saleProduct' />
        </div>
    );
}

export default SaleProduct;
