import "./RegistrationForm.css";
import { postApi } from "../api/api";
import { useState } from "react";

function RegistrationForm() {
  const [values, setValues] = useState({
    name: "",
    introduction: "",
    price: "",
    tag: "",
  });
  const [tagArray, setTageArray] = useState([]);
  const [conditionError, setConditionError] = useState({
    name: false,
    introduction: false,
    price: false,
    tag: false,
  });

  // input 값
  const handleChange = (event) => {
    const { name, value } = event.target;
    setValues((preValues) => ({
      ...preValues,
      [name]: value,
    }));
  };

  // const handleKeyNumber = e.key === "e" || e.key === "E" || e.key === "-" || e.key === "+"

  // 포커스 이동 시 에러 함수 (간소화 가능할 듯)
  const handleBlur = (e) => {
    const { name, value } = e.target;
    if (name === "title") {
      if (value.length > 10) {
        setConditionError((preConditionError) => ({
          ...preConditionError,
          title: true,
        }));
      } else if (conditionError.title === true && value.length <= 10) {
        setConditionError((preConditionError) => ({
          ...preConditionError,
          title: false,
        }));
      }
    } else if (name === "introduction") {
      if (value.length < 10) {
        setConditionError((preConditionError) => ({
          ...preConditionError,
          introduction: true,
        }));
      } else if (conditionError.introduction === true && value.length >= 10) {
        setConditionError((preConditionError) => ({
          ...preConditionError,
          introduction: false,
        }));
      }
    } else if (name === "price") {
      if (!/^\d*$/.test(value)) {
        setConditionError((preConditionError) => ({
          ...preConditionError,
          introduction: true,
        }));
      } else if (conditionError.introduction === true && value.length >= 10) {
        setConditionError((preConditionError) => ({
          ...preConditionError,
          introduction: false,
        }));
      }
    }
  };

  return (
    <form className="RegistrationFormContaner">
      <div className="productRegistration">
        <p className="productRegistrationLabel">상품 등록하기</p>
        <button className="productRegistrationButton">등록</button>
      </div>
      <div id="registrationName" className="Registrationbox">
        <label htmlFor="productName" className="RegistrationLabel">
          상품명
        </label>
        <input
          name="name"
          value={values.name}
          id="productName"
          className="productInput"
          placeholder="상품명을 입력해주세요"
          onChange={handleChange}
        />
      </div>
      {conditionError.name && <p>10자 이내로 입력해주세요</p>}
      <div id="registrationIntroduction" className="Registrationbox">
        <label htmlFor="productIntroduction" className="RegistrationLabel">
          상품 소개
        </label>
        <textarea
          name="introduction"
          value={values.introduction}
          id="productIntroduction"
          className="productInput"
          placeholder="상품 소개를 입력해주세요"
          onChange={handleChange}
          onBlur={handleBlur}
        />
      </div>
      <div className="Registrationbox">
        <label htmlFor="productPrice" className="RegistrationLabel">
          판매가격
        </label>
        <input
          name="price"
          value={values.price}
          id="productPrice"
          className="productInput"
          placeholder="판매 가격을 입력해주세요"
          onChange={handleChange}
        />
      </div>
      <div className="Registrationbox">
        <label htmlFor="productTag" className="RegistrationLabel">
          태그
        </label>
        <input
          name="tag"
          value={values.tag}
          id="productTag"
          className="productInput"
          placeholder="태그를 입력해주세요"
          onChange={handleChange}
        />
      </div>
    </form>
  );
}

export default RegistrationForm;
