import './BestProductStyle.css'
import { useState, useEffect } from "react";
import ProductCard from "./ProductCard";
import api from "./api";

function BestProduct() {
    const [ items, setItems ] = useState([])
    const [ logingError, setLodingError ] = useState(null)

    //api 호출
    const handleItemList = async (params) => {
        try {
            setLodingError(null);
            const { list } = await api(params);
            setItems(list);
        } catch(e) {
            setLodingError(e);
            return;
        }
    };

    //첫 랜더링 시 실행
    useEffect(() => {
        handleItemList({orderBy: 'favorite', pageSize: 4})
    }, [])

    return (
        <div className='bestProductContaner'>
            {logingError?.message && <span>{logingError.message}</span>}
            {logingError?.message || <h2 className='productFont bestProductFont'>베스트 상품</h2>}
            {logingError?.message || <ProductCard items={items} variant='bestProduct' />}
        </div>
    );
}

export default BestProduct;
