import { useState, useEffect } from "react";
import ProductCard from "./ProductCard";
import api from "./api";

function BestProduct() {
    const [ items, setItems ] = useState([])
    const [ logingError, setLodingError ] = useState(null)
    const [ logingErrorTag, setLodingErrorTag] = useState(false)

    //api 호출
    const handleItemList = async (params) => {
        try {
            setLodingError(null);
            setLodingErrorTag(false)
            const { list } = await api(params);
            setItems(list);
        } catch(e) {
            setLodingError(e);
            setLodingErrorTag(true)
            return;
        }
    };

    //첫 랜더링 시 실행
    useEffect(() => {
        handleItemList({orderBy: 'favorite', pageSize: 2})
    }, [])

    return (
        <>
        {logingError?.message && <span>{logingError.message}</span>}
        {logingErrorTag || 
        <div style={{minWidth:'500px'}}>
            <div className='bestProductContaner'>
                <h2 className='productFont bestProductFont'>베스트 상품</h2>
                <ProductCard items={items} variant='bestProduct' />
            </div>

        </div>
        }
        </>
    );
}

export default BestProduct;
