import { useState } from "react";

function useAsync (asyncFunc) {
    const [items, setItems] = useState([]);
    const [lodingError, setLodingError] = useState(null);
    const [lodingErrorTag, setLodingErrorTag] = useState(false);
    const [total, setTotal] = useState(0);

    const wrappedFunction = async (...agrs) => {
        try {
            setLodingError(null);
            setLodingErrorTag(false);
            const { totalCount, list } = await a(...agrs);
            setItems(list);
            setTotal(totalCount);
        } catch (e) {
            setLodingError(e);
            setLodingErrorTag(true);
            return;
        }
    };

    return [items, lodingError, lodingErrorTag, total, wrappedFunction]
}

export default useAsync
