import './SaleProductStyle.css'
import { useState, useEffect } from "react";
import Pagination from "react-js-pagination"; //라이브러리 다운 받았습니다
import ProductCard from "./ProductCard";
import SearchForm from './SearchForm';
import Registration from './Registration'
import OrderChange from './OrderChange';
import api from "./api";
import searchIcon from './img/searchIcon.png'

function SaleProduct() {
    const [ items, setItems ] = useState([])
    const [ order, setOrder ] = useState('recent')
    const [ orderName, setOrderName ] = useState('최신순')
    const [ search, setSeaerch ] = useState('')
    const [ productSearch, setProductSearch ] = useState('')
    const [ page, setPage ] = useState(1);
    const [ total, setTotal ] = useState(0)

    //api 호출
    const handleItemList = async (params) => {
        try {
            setLodingError(null);
            setLodingErrorTag(false)
            const { list } = await api(params);
            setItems(list);
        } catch(e) {
            setLodingError(e);
            setLodingErrorTag(true)
            return;
        }
    };

    //첫 랜더링 시 실행
    useEffect(() => {
        if(productSearch === '') {
            handleItemList({orderBy: order, page: page, pageSize: 10});
        } else {
            handleItemList({keyword: productSearch});
            setSeaerch('');
        }

    }, [order, page, productSearch])

    //order 변경 함수
    const handleOrderChange = {
        orderChangeRecent: function() {
            setOrder('recent');
            setOrderName('최신순')
        },

        orderChangeFavorite: function() {
            setOrder('favorite');
            setOrderName('좋아요순')
        }
    }

    //search input value 변경 함수 
    const handleSearch = (e) => {
        setSeaerch(e.target.value);
    }

    const keyDownSearch = (e) => {
        if(e.key === 'Enter') {
            setProductSearch(search);
        }
    }

    //페이지 변경 함수
    const handlePageChange = (page) => {
        window.scrollTo(0, 0);
        setPage(page);
    };


    return (
        <div className='saleProductContaner'>
            <div className='saleProductParts'>
                <h2 className='productFont saleProductFont'>판매 중인 상품</h2>
                <div className='saleProductFrom'>
                    <div className='searchFromPositin'>
                        <img className='readingGlasses' src={searchIcon} alt='돋보기'/>
                        <SearchForm value={search} onChange={handleSearch} onKeyPress={keyDownSearch} />
                    </div>
                    <Registration />
                    <OrderChange orderName={orderName} onClick={handleOrderChange}/>
                </div>
            </div>
            <ProductCard items={items} variant='saleProduct' />
            <Pagination
                activePage={page} // 현재 페이지
                itemsCountPerPage={10} // 한 페이지랑 보여줄 아이템 갯수
                totalItemsCount={total} // 총 아이템 갯수
                pageRangeDisplayed={5} // paginator의 페이지 범위
                prevPageText={"‹"} // "이전"을 나타낼 텍스트
                nextPageText={"›"} // "다음"을 나타낼 텍스트
                onChange={handlePageChange} // 페이지 변경을 핸들링하는 함수 
            />
        </div>
    );
}

export default SaleProduct;
